html, body {
    padding: 0;
    margin: 0;
    background-color: white;
}

.body {
    width: calc(100vw - 340px);
    height: 100vh;
    overflow: hidden;
    position: absolute;
    top: 0;
    left: 0;
    background-color: #000;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: justify;
    -webkit-justify-content: space-between;
        -ms-flex-pack: justify;
            justify-content: space-between;
}

.chatroom {
    position: absolute;
    right: 0;
    top: 0;
    height: 100%;
    width: 340px;
    box-sizing: border-box;
    padding: 40px 20px;
    border: 1px solid #000;
}

.chatroom .chatlist {
    position: relative;
    padding: 0;
    margin: 0 0 20px;
    width: 100%;
    height: calc(100% - 80px);
    background-color: white;
    list-style: none;
    font-size: 1em;
    line-height: 1.6em;
    overflow: scroll;
}

.chatroom .chatlist .chat {
    list-style-position: unset;
    overflow: hidden;
    word-wrap: break-word;
}

.chatroom .send-msg-form {
    position: relative;
    width: 100%;
}

.chatroom .send-msg-form .send-msg-input {
    width: 100%;
    box-sizing: border-box;
    height: 50px;
    font-size: 16px;
    line-height: 1.5em;
    padding: 2px 5px;
}

.chatroom .send-msg-form .send-msg-submit {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    margin: auto;
}

.chatroom .send-msg-form .new-msg-hint {
    position: absolute;
    top: -40px;
    width: 100%;
    text-align: center;
    height: 40px;
    line-height: 40px;
    background-color: rgba(255, 255, 255, 0.8);
}

.monitors {
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-direction: column;
        -ms-flex-direction: column;
            flex-direction: column;
    -webkit-box-align: end;
    -webkit-align-items: flex-end;
        -ms-flex-align: end;
            align-items: flex-end;
    background-color: #000;
    -webkit-flex-wrap: nowrap;
        -ms-flex-wrap: nowrap;
            flex-wrap: nowrap;
    height: 100%;
    width: 208px;
    -webkit-align-self: flex-end;
        -ms-flex-item-align: end;
            align-self: flex-end;
}

.monitor {
    height: 117px;
    width: 208px;
    float: right;
    display: block;
    position: relative;
    cursor: pointer;
}

.monitor img {
    width: 100%;
}

.monitor p {
    position: absolute;
    z-index: 10;
    top: 0;
    left: 0;
    margin: 0;
}

.monitor.active {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0;
    bottom: 0;
    margin: auto 0;
}

.player {
    position: relative;
    top: 0;
    left: 0;
    bottom: 0;
    margin: auto;

}
