@import url(https://fonts.googleapis.com/css?family=Roboto);html, body {
    padding: 0;
    margin: 0;
    background-color: white;
    overflow: hidden;
}

* {
    font-family: 'Roboto';
}

#root {
    width: 100vw;
    height: 100vh;
    overflow: hidden;
}

.body {
    width: 100vw;
    height: 100vh;
    overflow: hidden;
    position: absolute;
    top: 0;
    left: 0;
    background-color: #000;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: justify;
    -webkit-justify-content: space-between;
        -ms-flex-pack: justify;
            justify-content: space-between;
    -webkit-transition: width 0.5s ease-out;
    transition: width 0.5s ease-out;
}

.body > .before, .body > .after {
    content: '';
    position: absolute;
    height: 30px;
    background-color: rgba(0, 0, 0, 0.3);
    width: 208px;
    right: 0;
    display: block;
    z-index: 10
}

.body > .before:after, .body > .after:after{
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    margin: auto;
    height: 0;
    width: 0;
    border-left: 8px solid transparent;
    border-right: 8px solid transparent;
}

.body > .before {
    top: 0
}

.body > .before:after{
    border-bottom: 8px solid rgba(255, 255, 255, 0.7);
}

.body > .after {
    bottom: 0
}

.body > .after:after{
    border-top: 8px solid rgba(255, 255, 255, 0.7);
}

.body.showChat{
    width: calc(100vw - 340px);
}

.chatroom {
    position: absolute;
    right: -340px;
    top: 0;
    height: 100%;
    width: 340px;
    box-sizing: border-box;
    padding: 40px 20px;
    -webkit-transition: right 0.5s ease-out;
    transition: right 0.5s ease-out;
}

.chatroom .toggleShowChat {
    position: absolute;
    top: 0;
    left: -28px;
    width: 25px;
    height: 25px;
    background-color: #FFF;
    border: 2px solid #555;
    -webkit-transition: left 0.5s ease-out;
    transition: left 0.5s ease-out;
    cursor: pointer;
    z-index: 15;
}

.chatroom .toggleShowChat i {
    color: #555;
    -webkit-transition: -webkit-transform 0.5s ease-out;
    transition: -webkit-transform 0.5s ease-out;
    transition: transform 0.5s ease-out;
    transition: transform 0.5s ease-out, -webkit-transform 0.5s ease-out;
}

.chatroom .chatlist {
    position: relative;
    padding: 0;
    margin: 0 0 20px;
    width: 100%;
    height: calc(100% - 80px);
    background-color: white;
    list-style: none;
    font-size: 1em;
    line-height: 1.6em;
    overflow: scroll;
    border: 1px solid #CCC;
    padding: 5px;
    box-sizing: border-box;
}

.chatroom .chatlist .chat {
    list-style-position: unset;
    overflow: hidden;
    word-wrap: break-word;
    display: block;
    -webkit-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    min-height: 30px;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    -ms-grid-row-align: center;
    align-items: center;
}

.chatroom .chatlist .chat .send-time {
    position: absolute;
    right: 3px;
    line-height: 30px;
    font-size: 12px;
    color: #AAA;
    opacity: 0;
    -webkit-transition: opacity 0.2s ease-out, background-color 0.2s ease-out;
    transition: opacity 0.2s ease-out, background-color 0.2s ease-out;
}

.chatroom .chatlist .chat .chat-monitor {
    text-decoration: none;
    color: #555;
    display: inline-block;
    height: 21px;
    width: 30px;
    position: relative;
    margin-right: 3px;
}

.chatroom .chatlist .chat .chat-monitor i {
    color: #555;
    font-size: 30px;
    cursor: pointer;
    position: absolute;
    left: 0;
    top: 0;
}

.chatroom .chatlist .chat .chat-monitor .text {
    position: absolute;
    left: 0;
    right: 0;
    margin-left: 0;
    margin-right: 0;
    margin-left: -5px;
    text-align: center;
    top: 2px;
    font-size: 12px;
    color: #FFF;
}

.chatroom .chatlist .chat .chat-monitor:hover .before{
    background-color: #888;
}

.chatroom .chatlist .chat .chat-monitor:hover .after{
    border-right: 9px solid #888;
}

.chatroom .chatlist .chat .user, .chatroom .chatlist .chat .content {
    line-height: 30px;
}

.chatroom .chatlist .chat.inActive, .chatroom .chatlist .chat.inActive > span{
    color: #CCC !important;
}

.chatroom .chatlist .chat.inActive .chat-monitor i, .chatroom .chatlist .chat.inActive > span .chat-monitor i{
    color: #CCC;
}

.chatroom .chatlist .chat.inActive .chat-monitor .text, .chatroom .chatlist .chat.inActive > span .chat-monitor .text{
    color: #FFF;
}

.chatroom .chatlist .chat:hover .send-time{
    opacity: 1;
    background-color: rgba(255, 255, 255, 0.9);
}

.chatroom .send-msg-form {
    position: relative;
    width: 100%;
    height: 70px;
}

.chatroom .send-msg-form .send-msg-input {
    width: 100%;
    box-sizing: border-box;
    height: 70px;
    font-size: 16px;
    line-height: 1.5em;
    padding: 2px 5px;
    padding-right: 50px;
    resize: none;
    border-color: #CCC;
}

.chatroom .send-msg-form .send-msg-submit {
    position: absolute;
    top: 0px;
    right: 1px;
    bottom: 0;
    margin: auto;
    width: 50px;
    box-sizing: border-box;
    border: 0;
    padding: 0;
    height: 68px;
    font-size: 16px;
    padding: 10px 5px;
    line-height: 1.5em;
    background-color: #8BC34A;
    color: #FFF;
}

.chatroom .send-msg-form .new-msg-hint {
    position: absolute;
    top: -40px;
    width: 100%;
    text-align: center;
    height: 40px;
    line-height: 40px;
    background-color: rgba(255, 255, 255, 0.8);
}

.chatroom.showChat{
    right: 0;
}

.chatroom.showChat .toggleShowChat{
    left: 0;
}

.chatroom.showChat .toggleShowChat i{
    -webkit-transform: rotate(180deg);
    transform: rotate(180deg);
}

.monitors {
    display: block;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-direction: column;
        -ms-flex-direction: column;
            flex-direction: column;
    -webkit-box-align: end;
    -webkit-align-items: flex-end;
        -ms-flex-align: end;
                -ms-grid-row-align: flex-end;
            align-items: flex-end;
    background-color: #000;
    -webkit-flex-wrap: nowrap;
        -ms-flex-wrap: nowrap;
            flex-wrap: nowrap;
    height: 100%;
    width: 208px;
    -webkit-align-self: flex-end;
        -ms-flex-item-align: end;
            align-self: flex-end;
    overflow: scroll;
    padding: 30px 0;
    box-sizing: border-box;
}

.monitor {
    height: 117px;
    width: 208px;
    float: right;
    display: block;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
        -ms-flex-pack: center;
            justify-content: center;
    -webkit-box-align: center;
    -webkit-align-items: center;
        -ms-flex-align: center;
                -ms-grid-row-align: center;
            align-items: center;
    position: relative;
    cursor: pointer;
    padding: 10px;
    box-sizing: border-box;
    background-position: center center;
    background-repeat: no-repeat;
    background-size: 100%;
    border: 5px solid rgba(255, 193, 6, 0.3);
    box-sizing: border-box;
    -webkit-transition: background-size 0.2s ease-out, border-color 0.2s ease-out;
    transition: background-size 0.2s ease-out, border-color 0.2s ease-out;
    overflow: hidden;
}

.monitor span {
    position: absolute;
    z-index: 10;
    top: 0px;
    margin: 0;
    background-color: rgba(0, 0, 0, 0.5);
    color: #FFF;
    padding: 5px 10px;
    font-size: 14px;
    line-height: 16px;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-transition: background-color 0.2s ease-out;
    transition: background-color 0.2s ease-out;
}

.monitor span i {
    font-size: 16px;
    margin-right: 3px;
}

.monitor span.monitor-id{
    left: 0;
}

.monitor span.online-counter{
    right: 0;
}

.monitor:hover, .monitor.active{
    background-size: 110%;
    border-color: rgba(255, 193, 6, 1);
}

.monitor:hover span, .monitor.active span{
    background-color: rgba(0, 0, 0, 0.8);
}

.playerWrapper {
    position: relative;
    top: 0;
    left: 0;
    bottom: 0;
    margin: auto;
    width: calc(100% - 208px);
    height: 100%;
}

.playerWrapper .playerBlocker {
    z-index: 100;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    height: 100%;
    width: 100%;
}

.playerWrapper .control-group {
    z-index: 101;
    position: absolute;
    bottom: 10%;
    left: 0;
    right: 0;
    margin: 0 auto;
    text-align: center;
}

.playerWrapper .control-group .left, .playerWrapper .control-group .right {
    border: 5px solid rgba(255, 193, 6, 0.3);
    width: 100px;
    height: 50px;
    display: inline-block;
    position: absolute;
    top: 0;
    bottom: 0;
    margin: auto;
    box-sizing: border-box;
    cursor: pointer;
    -webkit-transition: border-color 0.2s ease-out;
    transition: border-color 0.2s ease-out
}

.playerWrapper .control-group .left:hover, .playerWrapper .control-group .right:hover{
    border-color: rgba(255, 193, 6, 1);
}

.playerWrapper .control-group .left:hover:before, .playerWrapper .control-group .right:hover:before{
    background-color: rgba(255, 193, 6, 1);
}

.playerWrapper .control-group .left:hover:after, .playerWrapper .control-group .right:hover:after{
    border-color: rgba(255, 193, 6, 1);
}

.playerWrapper .control-group .left:before, .playerWrapper .control-group .right:before{
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    margin: auto;
    height: 5px;
    width: 30px;
    background-color: rgba(255, 193, 6, 0.3);
    -webkit-transition: background-color 0.2s ease-out;
    transition: background-color 0.2s ease-out;
}

.playerWrapper .control-group .left:after, .playerWrapper .control-group .right:after{
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    margin: auto;
    height: 20px;
    width: 20px;
    border-left: 5px solid rgba(255, 193, 6, 0.3);
    border-top: 5px solid rgba(255, 193, 6, 0.3);
    -webkit-transform-origin: center center;
    transform-origin: center center;
    -webkit-transition: border-color 0.2s ease-out;
    transition: border-color 0.2s ease-out;
}

.playerWrapper .control-group .left {
    left: 5%
}

.playerWrapper .control-group .left:after{
    -webkit-transform: translateX(-15px) rotate(-45deg);
    transform: translateX(-15px) rotate(-45deg);
}

.playerWrapper .control-group .right {
    right: 5%
}

.playerWrapper .control-group .right:after{
    -webkit-transform: translateX(-10px) rotate(135deg);
    transform: translateX(-10px) rotate(135deg);
}

.playerWrapper .control-group .interact {
    height: 50px;
    width: 120px;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    margin: auto;
    -webkit-transition: border-color 0.2s ease-out, color 0.2s ease-out;
    transition: border-color 0.2s ease-out, color 0.2s ease-out;
    border: 5px solid rgba(255, 193, 6, 0.3);
    color: rgba(255, 193, 6, 0.3);
    text-align: center;
    line-height: 50px;
    font-size: 30px;
    letter-spacing: 0.1em;
    cursor: pointer
}

.playerWrapper .control-group .interact:hover{
    border-color: rgba(255, 193, 6, 1);
    color: rgba(255, 193, 6, 1);
}
.player {
    position: relative;
    top: 0;
    left: 0;
    bottom: 0;
    margin: auto;
    width: 100%;
    height: 100%;
}
