html, body {
    padding: 0;
    margin: 0;
    background-color: white;
    overflow: hidden;
}

#root {
    width: 100vw;
    height: 100vh;
    overflow: hidden;
}

.body {
    width: 100vw;
    height: 100vh;
    overflow: hidden;
    position: absolute;
    top: 0;
    left: 0;
    background-color: #000;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: justify;
    -webkit-justify-content: space-between;
        -ms-flex-pack: justify;
            justify-content: space-between;
    -webkit-transition: width 0.5s ease-out;
    transition: width 0.5s ease-out
}

.body.showChat {
    width: calc(100vw - 340px);
}

.chatroom {
    position: absolute;
    right: -340px;
    top: 0;
    height: 100%;
    width: 340px;
    box-sizing: border-box;
    padding: 40px 20px;
    border: 1px solid #555;
    -webkit-transition: right 0.5s ease-out;
    transition: right 0.5s ease-out;
}

.chatroom .toggleShowChat {
    position: absolute;
    top: 0;
    left: -28px;
    width: 25px;
    height: 25px;
    background-color: #FFF;
    border: 1px solid #555;
    -webkit-transition: left 0.5s ease-out;
    transition: left 0.5s ease-out;
    cursor: pointer;
}

.chatroom .toggleShowChat .after {
    position: absolute;
    height: 10px;
    width: 10px;
    border-left: 1px solid #555;
    border-right: 0;
    border-top: 1px solid #555;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    margin: auto;
    -webkit-transform: translateX(20%) rotate(-45deg);
    transform: translateX(20%) rotate(-45deg);
    -webkit-transition: -webkit-transform 0.5s ease-out;
    transition: -webkit-transform 0.5s ease-out;
    transition: transform 0.5s ease-out;
    transition: transform 0.5s ease-out, -webkit-transform 0.5s ease-out;
}

.chatroom .chatlist {
    position: relative;
    padding: 0;
    margin: 0 0 20px;
    width: 100%;
    height: calc(100% - 80px);
    background-color: white;
    list-style: none;
    font-size: 1em;
    line-height: 1.6em;
    overflow: scroll;
    border: 1px solid #CCC;
    padding: 5px;
    box-sizing: border-box;
}

.chatroom .chatlist .chat {
    list-style-position: unset;
    overflow: hidden;
    word-wrap: break-word;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    height: 30px;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
}

.chatroom .chatlist .chat .chat-monitor {
    text-decoration: none;
    color: #FFF;
    display: inline-block;
    height: 30px;
    width: 30px;
    text-align: center;
    position: relative;
    margin-right: 3px;
}

.chatroom .chatlist .chat .chat-monitor .before {
    position: absolute;
    height: 17px;
    width: 20px;
    top: 0;
    bottom: 0;
    margin: auto 0;
    background-color: #555;
    border-radius: 3px;
    left: 1px;
    -webkit-transition: background-color 0.2s ease-out;
    transition: background-color 0.2s ease-out;
}

.chatroom .chatlist .chat .chat-monitor .after {
    position: absolute;
    height: 0;
    width: 0;
    right: 3px;
    top: 0;
    bottom: 0;
    margin: auto 0;
    border-top: 7px solid transparent;
    border-bottom: 7px solid transparent;
    border-right: 9px solid #555;
    -webkit-transition: border-right 0.2s ease-out;
    transition: border-right 0.2s ease-out;
}

.chatroom .chatlist .chat .chat-monitor .text {
    position: absolute;
    left: 0;
    right: 0;
    margin-left: 0;
    margin-right: 0;
    margin-left: -8px;
    top: 4px;
    font-size: 12px;
}

.chatroom .chatlist .chat .chat-monitor:hover .before {
    background-color: #888;
}

.chatroom .chatlist .chat .chat-monitor:hover .after {
    border-right: 9px solid #888;
}

.chatroom .send-msg-form {
    position: relative;
    width: 100%;
    height: 70px;
}

.chatroom .send-msg-form .send-msg-input {
    width: 100%;
    box-sizing: border-box;
    height: 70px;
    font-size: 16px;
    line-height: 1.5em;
    padding: 2px 5px;
    padding-right: 50px;
    resize: none;
    border-color: #CCC;
}

.chatroom .send-msg-form .send-msg-submit {
    position: absolute;
    top: 0px;
    right: 1px;
    bottom: 0;
    margin: auto;
    width: 50px;
    box-sizing: border-box;
    border: 0;
    padding: 0;
    height: 68px;
    font-size: 16px;
    padding: 10px 5px;
    line-height: 1.5em;
    background-color: #8BC34A;
    color: #FFF;
}

.chatroom .send-msg-form .new-msg-hint {
    position: absolute;
    top: -40px;
    width: 100%;
    text-align: center;
    height: 40px;
    line-height: 40px;
    background-color: rgba(255, 255, 255, 0.8);
}

.chatroom.showChat {
    right: 0;
}

.chatroom.showChat .toggleShowChat {
    left: 0;
}

.chatroom.showChat .toggleShowChat .after {
    -webkit-transform: translateX(-20%) rotate(135deg);
    transform: translateX(-20%) rotate(135deg);
}

.monitors {
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-direction: column;
        -ms-flex-direction: column;
            flex-direction: column;
    -webkit-box-align: end;
    -webkit-align-items: flex-end;
        -ms-flex-align: end;
            align-items: flex-end;
    background-color: #000;
    -webkit-flex-wrap: nowrap;
        -ms-flex-wrap: nowrap;
            flex-wrap: nowrap;
    height: 100%;
    width: 208px;
    -webkit-align-self: flex-end;
        -ms-flex-item-align: end;
            align-self: flex-end;
    overflow: scroll;
}

.monitor {
    height: 117px;
    width: 208px;
    float: right;
    display: block;
    position: relative;
    cursor: pointer;
}

.monitor img {
    width: 100%;
}

.monitor p {
    position: absolute;
    z-index: 10;
    top: 0;
    left: 0;
    margin: 0;
    background-color: rgba(0, 0, 0, 0.8);
    color: #FFF;
    padding: 5px 10px;
}

.monitor.active {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0;
    bottom: 0;
    margin: auto 0;
}

.playerWrapper {
    position: relative;
    top: 0;
    left: 0;
    bottom: 0;
    margin: auto;
    width: calc(100% - 208px);
    height: 100%;
}

.playerWrapper .playerBlocker {
    z-index: 100;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    height: 100%;
    width: 100%;
}

.playerWrapper .control-group {
    z-index: 101;
    position: absolute;
    bottom: 10%;
    left: 0;
    right: 0;
    margin: 0 auto;
    text-align: center;
}

.playerWrapper .control-group .left, .playerWrapper .control-group .right {
    background-color: #FFF;
    width: 100px;
    height: 50px;
    display: inline-block;
}

.playerWrapper .control-group .left {
    margin-right: 50px;
}

.playerWrapper .control-group .right {
    margin-left: 50px;
}
.player {
    position: relative;
    top: 0;
    left: 0;
    bottom: 0;
    margin: auto;
    width: 100%;
    height: 100%;
}
